import { useState } from "react";

export const usePushWork = (validator) => {
  const [value, setValue] = useState([]);
  const onSubmitWork = (event) => {
    const workName = event.target.workName.value;
    const workTime = event.target.workTime.value;
    const workObj = { work: workName, time: workTime };
    console.log(workObj);
    let willUpdate = true;
    if (typeof validator === "function") willUpdate = validator(workObj);
    if (willUpdate) {
      setValue(value.push(workObj));
    }
    // return value;
  };
  return { value, onSubmitWork };
};
