import { AddWorkWindow } from "./window/AddWindow";
import { WorkList } from "./WorkList";
const validatorFun = (workObj) => {
  return workObj.workName && workObj.workTime;
};
const AddWakeTimeWindow = () => {
  const onRecordTime = (event) => {
    // event.preventDefault();
    // this.forceUpdate();
    const [hour, minute] = new Date()
      .toTimeString()
      .split(":")
      .slice(0, 2)
      .map((numChar) => parseInt(numChar));
    window.localStorage.setItem("wakeTime", JSON.stringify({ hour, minute }));
  };
  const wakeTimeElem = useClick(onRecordTime);
  return <button ref={wakeTimeElem}>SUBMIT</button>;
};
const AddBedTimeWindow = () => {
  const onRecordTime = (event) => {
    // event.preventDefault();
    const [hour, minute] = new Date()
      .toTimeString()
      .split(":")
      .slice(0, 2)
      .map((numChar) => parseInt(numChar));
    window.localStorage.setItem("bedTime", JSON.stringify({ hour, minute }));
  };
  const BedTimeElem = useClick(onRecordTime);
  return <button ref={BedTimeElem}>SUBMIT</button>;
};
export const WorkListTemplate = () => {
  return (
    <div>
      <h1>Hamburger App</h1>
      <AddWakeTimeWindow />
      <AddWorkWindow />
      <WorkList />
      <AddBedTimeWindow />
      <ol>
        <li>Wake Up at 08:00 </li>
        <li>Clean My room 3h </li>
        <li>Do My homework 3h 20m </li>
        <li>Test comment 2h 1m </li>
        <li>Sleep at 01:00 </li>
      </ol>
    </div>
  );
};
