import StarRatings from "react-star-ratings";
import { useWorkListGrade } from "../../../hooks/workList/useWorkListGrade";
import { faStar, faStarHalf } from "@fortawesome/free-solid-svg-icons";
import {} from "@fortawesome/free-regular-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
const starElement = (
  <FontAwesomeIcon className="work-icon__star" icon={faStar} size="1x" />
);
const starHalfElement = (
  <FontAwesomeIcon
    className="work-icon__star-half"
    icon={faStarHalf}
    size="1x"
  />
);
export const WorkListGrade = ({ user, date }) => {
  const [grade, difference, target, value, isGradeLoading, updateInfo] =
    useWorkListGrade(user, date);
  const getGradeSum = () =>
    grade
      ? Object.values(grade).reduce((prev, value) => (prev += value), 0)
      : 0;
  const getStar = (grade) =>
    new Array(5).fill().map((elem, idx) => {
      return 2 * (idx + 1) < grade
        ? "꽉"
        : 2 * (idx + 1) - grade === 1
        ? "반"
        : "빈";
    });
  const gradeElement = new Array(5).fill().map((elem, idx) => {});
  const tooltipElement = (
    <div className="grade-tooltip-content">
      <div className="grade-tooltip-box">test</div>
    </div>
  );

  const getGradeBtn = <button onClick={updateInfo}> Get Grade! </button>;
  const displayGrade = (
    <div className="tooltip">
      {" "}
      {starElement}
      {starHalfElement}
      {/* <FontAwesomeIcon icon={faCoffee} /> */}
      {gradeElement} {tooltipElement} | update{" "}
    </div>
  );
  return grade ? displayGrade : getGradeBtn;
};
