import Modal from "react-modal";
import { useState } from "react";
import { usePushWork } from "../../hooks/usePushWork";
import { AddWorkWindow } from "./window/AddWindow";
import { WorkList } from "./WorkList";
import { WorkListTemplate } from "./WorkListTemplate";
import { useEditWork } from "../../hooks/useEditWork";
import { EditWorkWindow } from "./window/EditWindow";
import { useUpdateWork } from "../../hooks/useUpdateWork";
Modal.setAppElement("#root");
const customStyles = {
  content: {
    // top: "40vh",
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    backgroundColor: "rgb(245, 235, 220)",
    border: "none",
  },
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "rgba(0,0,0,0.4)",
  },
};

export const WorkListComponent = () => {
  const [workList, setWorkList] = useUpdateWork([]);
  const [addModalIsOpen, setAddModalIsOpen] = useState(false);
  const openAddModal = () => setAddModalIsOpen(true);
  const closeAddModal = () => setAddModalIsOpen(false);

  const { onSubmitColor, onSubmitWork } = usePushWork(
    workList,
    setWorkList,
    closeAddModal
  );

  const addWorkWindow = (
    <AddWorkWindow
      workList={workList}
      onSubmitColor={onSubmitColor}
      onSubmitWork={onSubmitWork}
    />
  );
  const addModal = (
    <Modal
      isOpen={addModalIsOpen}
      onRequestClose={closeAddModal}
      style={customStyles}
      contentLabel="Example Modal"
      overlayClassName="Overlay"
    >
      {addWorkWindow}
      <button
        className="modalWindow__close modalWindow__btn"
        onClick={closeAddModal}
      >
        CLOSE
      </button>
    </Modal>
  );

  console.log("In worklist, list: ", workList);
  const workListDisplay = (
    <WorkList
      workList={workList}
      addModal={addModal}
      openAddModal={openAddModal}
    />
  );
  return <WorkListTemplate workList={workListDisplay} />;
};
