import Modal from "react-modal";
import { useState } from "react";
import { usePushWork } from "../../hooks/usePushWork";
import { AddWorkWindow } from "./window/AddWindow";
import { WorkList } from "./WorkList";
import { WorkListTemplate } from "./WorkListTemplate";
import { useEditWork } from "../../hooks/useEditWork";
Modal.setAppElement("#root");
const customStyles = {
  content: {
    // top: "40vh",
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    backgroundColor: "rgb(245, 235, 220)",
    border: "none",
  },
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "rgba(0,0,0,0.4)",
  },
};
const initAddModal = () => {
  const [addModalIsOpen, setAddModalIsOpen] = useState(false);
  const openAddModal = () => {
    console.log("OPEN", addModalIsOpen);
    setAddModalIsOpen(true);
    console.log("OPEN", addModalIsOpen);
  };
  const closeAddModal = () => {
    console.log("CLOSE1", addModalIsOpen);
    setAddModalIsOpen(false);
    console.log("CLOSE2", addModalIsOpen);
  };
  return { addModalIsOpen, setAddModalIsOpen, openAddModal, closeAddModal };
};
export const WorkListComponent = () => {
  let { workList, onSubmitColor, onSubmitWork } = usePushWork(closeAddModal);
  const {
    workListEdit = workList,
    onEditColor,
    onEditWork,
  } = useEditWork(workList);
  workList = workListEdit;
  const addWorkWindow = (
    <AddWorkWindow
      workList={workList}
      onSubmitColor={onSubmitColor}
      onSubmitWork={onSubmitWork}
    />
  );
  const addModal = (
    <Modal
      isOpen={addModalIsOpen}
      onRequestClose={closeAddModal}
      style={customStyles}
      contentLabel="Example Modal"
      overlayClassName="Overlay"
    >
      {addWorkWindow}
      <button
        className="addWindow__close addWindow__btn"
        onClick={closeAddModal}
      >
        {" "}
        CLOSE
      </button>
    </Modal>
  );

  console.log("In worklist, list: ", workList);
  const workListDisplay = (
    <WorkList
      workList={workList}
      addWorkWindow={addWorkWindow}
      modal={addModal}
      openModal={openAddModal}
    />
  );
  return <WorkListTemplate workList={workListDisplay} />;
};
