export const useDeleteWork = (workList, setWorkList, callback) => {
  const onDeleteWork = (event, idx) => {
    const deleteAction = () => {
      callback();
      workList.splice(idx, 1);
      setWorkList(workList);
    };
    const cancelAction = () => {
      console.log("CANCEL DELETE WORK");
    };
  };
  return { onDeleteWork };
};
