export const onRecordTime = (event) => {
  event.preventDefault();
  const [hour, minute] = new Date().toTimeString().split(":");
  console.log("INPUT VALUE: ", hour, minute);
};
export const AddWorkWindow = () => {
  const validatorFun = (workObj) => {
    return workObj.workName && workObj.workTime;
  };
  const { workList, onSubmitWork } = usePushWork(validatorFun);
  localStorage.setItem("workList", JSON.stringify(workList));
  return (
    <form onSubmit={onSubmitWork}>
      {WorkList.workList}
      <input type="text" name="workName" placeholder="추가할 작업명"></input>
      <input type="number" name="workTime" placeholder="작업 시간"></input>
      <button type="submit">SUBMIT</button>
    </form>
  );
};

