import { useClick } from "../../../hooks/useClick";
import { usePushWork } from "../../../hooks/usePushWork";
import { WorkList } from "../WorkList";

export const OnSubmitWorkHook = (event) => {};

export const AddWorkWindow = () => {
  const { workList, onSubmitWork } = usePushWork(validatorFun);

  if (workList.length)
    window.localStorage.setItem("workList", JSON.stringify(workList));
  return (
    <form onSubmit={onSubmitWork}>
      {/* {WorkList.workList} */}
      <input type="text" name="workName" placeholder="추가할 작업명"></input>
      <input type="number" name="workTime" placeholder="작업 시간"></input>
      <button type="submit">SUBMIT</button>
    </form>
  );
};

