import { useState } from "react";

export const WorkList = () => {
  const workList = JSON.parse(localStorage.getItem("workList"));
  console.log("In worklist, list: ", workList);

  const makeWorkItem = (workItem) => {
    return (
      <li>
        {workItem.workName} {workItem.workTime}h
      </li>
    );
  };
  const useForceUpdate = () => {
    const [value, setValue] = useState(0);
    console.log("UPDATE");
    return () => setValue(value + 1);
  };
  window.addEventListener("storage", useForceUpdate);
  const wakeTimeObj = JSON.parse(localStorage.getItem("wakeTime"));
  const bedTimeObj = JSON.parse(localStorage.getItem("bedTime"));

  return (
    <ol>
      <li>
        {wakeTimeObj
          ? `${
              wakeTimeObj.hour < 10 ? "0" + wakeTimeObj.hour : wakeTimeObj.hour
            }:${
              wakeTimeObj.minute < 10
                ? "0" + wakeTimeObj.minute
                : wakeTimeObj.minute
            }`
          : "Record Wake Time"}
      </li>
      {workList &&
        workList.map((workItem) => {
          return makeWorkItem(workItem);
        })}
      <li>
        {bedTimeObj
          ? `${
              bedTimeObj.hour < 10 ? "0" + bedTimeObj.hour : bedTimeObj.hour
            }:${
              bedTimeObj.minute < 10
                ? "0" + bedTimeObj.minute
                : bedTimeObj.minute
            }`
          : "Record Bed Time"}
      </li>
    </ol>
    // </div>
  );
};
