import axios from "axios";
import qs from "qs";
import { useEffect } from "react";
export const GithubLoginPage = ({ history, location, customLoginHook }) => {
  // const {code} =
  console.log("locat", location?.search);
  useEffect(() => {
    const { setUser } = customLoginHook;
    const getToken = async () => {
      const { code } = qs.parse(location.search, {
        ignoreQueryPrefix: true,
      });
      try {
        const { access_token } = await axios.post("/api/login/github", {
          code,
        });
        localStorage.setItem("access_token", access_token);
        history.push("/");
      } catch (error) {
        alert(error);
        history.push("/");
      }
    };
    getToken();
  }, [location, history]);
  return <h1>GITHUB LOGIN LOADING . . .</h1>;
};
