import Modal from "react-modal";
import { useState } from "react";
import { AddWorkWindow } from "../../window/AddWindow";
import "../../../../css/modam.css";
// Modal.setAppElement("#root");
const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    backgroundColor: "blue",
  },
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "rgba(0,0,0,0.4)",
  },
};
export const EmptyWork = ({ workList, onSubmitWork }) => {
  const [modalIsOpen, setIsOpen] = useState(false);
  const openModal = () => {
    setIsOpen(true);
  };
  const closeModal = () => {
    setIsOpen(false);
  };

  const addWorkWindow = (
    <AddWorkWindow workList={workList} onSubmitWork={onSubmitWork} />
  );
  return (
    <div>
      <li
        className={"empty-element workList__element-empty"}
        onClick={openModal}
      >
        Add your work
      </li>

      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
        overlayClassName="Overlay"
      >
        {addWorkWindow}
        <button onClick={closeModal}> CLOSE</button>
      </Modal>
    </div>
  );
};
