export class LocalStroage {
  static workList() {
    return {
      isEmpty: () =>
        localStorage.getItem("workList")
          ? !JSON.parse(window.localStorage.getItem("workList")).length
          : true,
      get: () =>
        localStorage.getItem("workList")
          ? JSON.parse(window.localStorage.getItem("workList"))
          : {},
      set: (workList) =>
        window.localStorage.setItem("workList", JSON.stringify(workList)),
    };
  }
  static recordTime(key) {
    return {
      isEmpty: () =>
        localStorage.getItem(key)
          ? !JSON.parse(window.localStorage.getItem(key)).length
          : true,
      get: () =>
        localStorage.getItem(key)
          ? JSON.parse(window.localStorage.getItem(key))
          : {},
      set: (timeObj) => {
        timeObj = JSON.stringify(timeObj);
        console.log("LOCAL TIME SET ", key, timeObj, typeof timeObj);
        window.localStorage.setItem(key, timeObj);
        window.localStorage.setItem("test", "123");
      },
    };
  }
}

