// import logo from './logo.svg';
import "./css/App.css";
import "./css/calendar.css";

import { changeFormatYYYYMMDD } from "./tools/time";

import { Route, Switch, BrowserRouter as Router } from "react-router-dom";
import { NotFoundPage } from "./components/pages/NotFoundPage";
import { LoginPage } from "./components/pages/LoginPage";
import { HomePage } from "./components/pages/HomePage";
import { Header } from "./components/Header";
import { useLogin } from "./hooks/user/useLogin";
import { useSetDate } from "./hooks/date/useSetDate";
import { GithubLoginPage } from "./components/pages/GithubLoginPage";
import { SignupPage } from "./components/pages/SignupPage";
import { SettingPage } from "./components/pages/SettingPage";
import { useUpdateSetting } from "./hooks/user/useUpdateSetting";

import Modal from "react-modal";
Modal.setAppElement("#root");
function App(props) {
  // useSetAxios();
  const updateDateHook = useSetDate();
  const date = changeFormatYYYYMMDD(updateDateHook[0], false);
  const customLoginHook = useLogin();
  const user = customLoginHook[0];
  const isLoggedIn = customLoginHook[2];
  console.log("[APP] USER: ", user);
  const updateSettingHook = useUpdateSetting(user);
  const { targetTimeObj } = updateSettingHook;

  return user === "Loading" ? (
    <h1> Loading in APP</h1>
  ) : (
    <Router>
      <Header date={date} customLoginHook={customLoginHook} />
      <main>
        {JSON.stringify(targetTimeObj)}
        <Switch>
          <Route
            exact
            path="/"
            render={() => (
              <HomePage
                user={user}
                targetTimeObj={targetTimeObj}
                updateDateHook={updateDateHook}
              />
            )}
          />
          <Route
            path="/login"
            render={({ history, location }) => {
              if (isLoggedIn) {
                history.push("/");
                // alert("You are already logged in.");
                return <NotFoundPage />;
              } else
                return (
                  <LoginPage
                    history={history}
                    location={location}
                    customLoginHook={customLoginHook}
                  />
                );
            }}
          />
          <Route
            path="/signup"
            render={({ history, location }) => {
              if (isLoggedIn) {
                history.push("/");
                // alert("You are already logged in.");
                return <NotFoundPage />;
              } else
                return <SignupPage history={history} location={location} />;
            }}
          />
          <Route
            path="/setting"
            render={({ history, location }) => {
              if (!isLoggedIn) {
                history.push("/");
                // alert("You are not logged in.");
                return <NotFoundPage />;
              } else
                return (
                  <SettingPage
                    user={user}
                    history={history}
                    location={location}
                    updateSettingHook={updateSettingHook}
                  />
                );
            }}
          />
          <Route
            path="/github-login"
            render={({ history, location }) => {
              if (isLoggedIn) {
                history.push("/");
                // alert("You are already logged in.");
                return <NotFoundPage />;
              } else
                return (
                  <GithubLoginPage
                    history={history}
                    location={location}
                    customLoginHook={customLoginHook}
                  />
                );
            }}
          />
          <Route component={NotFoundPage} />
        </Switch>
      </main>
      <footer>footer</footer>
    </Router>
  );
}

export default App;
