import qs from "qs";
import axios from "axios";
import isEmail from "validator/lib/isEmail";
import { useState } from "react";

export const useAttemptSignup = ({ history, locaiton }) => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const handleAttemptSignup = async () => {
    try {
      // event.preventDefault();
      //   const { code } = req.body;
      console.log("CONNET ON REACT-REGISTER page: ");
      console.log(username, email, password);
      if (!isEmail(email)) {
        throw "Unexpected Email Form. Please Retry.";
      }
      if (!username || !email || !password) {
        let errText = `[ERROR] ${username ? "" : "Username"}${
          !email + !password > 0 && !username ? ", " : ""
        }${email ? "" : "Email"}${!password > 0 && !email ? ", " : ""}${
          password ? "" : "Password"
        } ${!username + !email + !password > 1 ? "are" : "is"} not entered.`;
        throw errText;
      }
      console.log("BEF AXIOS");
      axios.interceptors.response.use(
        (res) => {
          return res;
        },
        (e) => {
          if (e.response.status === 401) {
            console.log("ER1ROR: ", e.response);

            throw `401 - Unauthorized\n${e.response?.data}`;
            return;
          }
          return Promise.reject(e);
        }
      );

      const response = await axios.post("/auth/signup", {
        username,
        email,
        password,
      });
      console.log("RES: ", response);
      alert("Sign up complete.");
      history.push("/");
    } catch (error) {
      console.error(error);
      alert(error, "lol");
    }
  };
  return {
    username,
    setUsername,
    email,
    setEmail,
    password,
    setPassword,
    handleAttemptSignup,
  };
};

