import { usePushWork } from "../../hooks/usePushWork";

let wakeTime, bedTime;
const workList = [];

export const isUploadWakeTime = () => wakeTime;

export const uploadWakeTime = (timeObj) => {
  wakeTime = timeObj;
};

export const uploadBedTime = (timeObj) => {
  bedTime = timeObj;
};

export const pushWork = (workObj) => {
  workList.push(workObj);
};

export const WorkList = () => {
  const validatorFun = (workObj) => {
    return workObj.workName && workObj.workTime;
  };
  const { workList, onSubmitWork } = usePushWork(validatorFun);

  const makeWorkItem = (workItem) => {
    return (
      <li>
        {workItem.workName} {workItem.workTime}
      </li>
    );
  };

  return (
    <div>
      <form onSubmit={onSubmitWork}>
        <input type="text" name="workName" placeholder="추가할 작업명"></input>
        <input type="number" name="workTime" placeholder="작업 시간"></input>
        <button type="submit">SUBMIT</button>
      </form>
      <ol>
        <li>
          {/* {isUploadWakeTime() Wake Up at {wakeTime.hour}:{wakeTime.minute}} */}
        </li>
        {workList.length}
        {workList.length &&
          workList.map((workItem) => {
            return makeWorkItem(workItem);
          })}
        <li>{/* Sleep at {bedTime.hour}:{bedTime.minute} */}</li>
      </ol>
    </div>
  );
};
