import { useState } from "react";

export const usePushWork = (validator) => {
  const [workList, setWorkList] = useState([]);
  const onSubmitWork = (event) => {
    event.preventDefault();
    const workName = event.target.workName.value;
    const workTime = event.target.workTime.value;
    const workObj = { workName, workTime };
    console.log(workObj);
    let willUpdate = true;
    if (typeof validator === "function") willUpdate = validator(workObj);
    if (willUpdate) {
      setWorkList((workList) => [...workList, workObj]);
      console.log(workList);
      localStorage.setItem("workList", JSON.stringify(workList));
    }
    // return workList;
  };
  return { workList, onSubmitWork };
};
