import { useEffect, useState } from "react";
import { API, APIv2 } from "../../tools/API";
import { divideDate } from "../../tools/time";
let befDate;
export const useDateInfo = (user, date) => {
  const [dateInfo, setDateInfo] = useState();
  const [isDateInfoLoad, setLoad] = useState(true);

  const getUserInfo = async () => {
    try {
      setLoad(true);
      const data = await APIv2.workList().getAll();
      if (!data?.data) throw new Error("Cannot found Your Work List");
      setDateInfo(data?.data);
      setLoad(false);
    } catch (error) {
      alert(error);
    }
  };
  useEffect(() => {
    if (
      divideDate(befDate).dividedAddressYYYYMM !==
        divideDate(date).dividedAddressYYYYMM &&
      user
    )
      getUserInfo();
    befDate = date;
  }, [user, date]);
  return dateInfo;
};
