import Modal from "react-modal";
import { useState } from "react";
import "../../../../css/modam.css";
Modal.setAppElement("#root");
const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    backgroundColor: "blue",
  },
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "rgba(0,0,0,0.4)",
  },
};
export const EmptyWork = ({ addWorkWindow }) => {
  const [modalIsOpen, setIsOpen] = useState(false);
  const openModal = () => {
    console.log("OPEN", modalIsOpen);
    setIsOpen(true);
    console.log("OPEN", modalIsOpen);
  };
  const toggleModal = () => {
    setIsOpen(!modalIsOpen);
  };
  const closeModal = () => {
    console.log("CLOSE1", modalIsOpen);
    setIsOpen(false);
    console.log("CLOSE2", modalIsOpen);
  };

  return (
    <li className={"empty-element workList__element-empty"} onClick={openModal}>
      Add your work
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
        overlayClassName="Overlay"
      >
        {addWorkWindow}
        <button onClick={closeModal}> CLOSE</button>
      </Modal>
    </li>
  );
};
