// import logo from './logo.svg';
import axios from "axios";
import { useState } from "react";
import Calendar from "react-calendar";
import "./App.css";
import { WorkListTemplate } from "./components/worklist/WorkListTemplate";
import { useAxios } from "./hooks/useAxios";
// import "react-calendar/dist/Calendar.css";
import "./css/calendar.css";

function App() {
  const [value, onChange] = useState(new Date());
  const clickDay = (event, value) => alert("Clicked day: ", value);
  const mark = ["09-12-2021", "09-13-2021", "09-14-2021"];
  return (
    <main>
      <article>
        <Calendar
          className="caldendar"
          onChange={onChange}
          value={value}
          locale={"en"}
          calendarType={"US"}
          onClickDay={(value, event) => console.log("Clic123ked day: ", value)}
          tileClassName={({ date, view }) => {
            if (mark.find((x) => x === date.format("DD-MM-YYYY"))) {
              return "highlight";
            }
          }}
          //DB에 있는 날짜는 체크표시 해야함.
        />
      </article>
      <article>
        <WorkListTemplate />
      </article>
    </main>
  );
}

export default App;
