import { useEffect, useState } from "react";
import { API, APIv2 } from "../../tools/API";
import { isEqualYYYYMMDateStr } from "../../tools/time";
let befDate;

export const useDateInfo = (user, date) => {
  const [dateInfo, setDateInfo] = useState();
  const [trigger, setTrigger] = useState();
  const [isDateInfoLoad, setLoad] = useState(true);

  const getUserInfo = async () => {
    try {
      setLoad(true);
      const data = await APIv2.workList(user, date).getAll();
      if (!data?.data) throw new Error("Cannot found Your Work List");
      setDateInfo(data?.data);
      setLoad(false);
    } catch (error) {
      alert(error);
    }
  };
  useEffect(() => {
    console.log("BEF: ", befDate, "CURR: ", date);
    if (user && (!befDate || !isEqualYYYYMMDateStr(befDate, date)))
      getUserInfo();
    befDate = date;
  }, [user, date, trigger]);
  return [dateInfo, isDateInfoLoad, setTrigger];
};
