export const WorkList = ({ workList }) => {
  // const [value, setValue] = useState(0);

  // const useForceUpdate = () => {
  //   console.log("UPDATE");
  //   return () => setValue(value + 1);
  // };
  // window.addEventListener("storage", useForceUpdate);
  const makeWorkItem = (workItem) => {
    return (
      <li>
        {workItem.workName} {workItem.workTime}h
      </li>
    );
  };

  return (
    <ol>
      <li>
        {wakeTime
          ? `${wakeTime.hour < 10 ? "0" + wakeTime.hour : wakeTime.hour}:${
              wakeTime.minute < 10 ? "0" + wakeTime.minute : wakeTime.minute
            }`
          : "Record Wake Time"}
      </li>
      {workList &&
        workList.map((workItem) => {
          return makeWorkItem(workItem);
        })}
      <li>
        {bedTime
          ? `${bedTime.hour < 10 ? "0" + bedTime.hour : bedTime.hour}:${
              bedTime.minute < 10 ? "0" + bedTime.minute : bedTime.minute
            }`
          : "Record Bed Time"}
      </li>
    </ol>
    // </div>
  );
};
