import axios from "axios";
import { useEffect, useState } from "react";
import { getToday } from "../../tools/time";
import { useUpdateTime } from "../workList/time/useUpdateTime";

const verifyToken = async () => {
  const { token } = JSON.parse(localStorage.getItem("access_token"));
  const response = await axios.post("/api/jwt/verify", { token });
  return response.data.decode;
};

export const useLogin = () => {
  const [user, setUser] = useState();
  const setUserInToken = async () => {
    try {
      if (
        !localStorage.getItem("access_token") ||
        localStorage.getItem("access_token") === "undefined"
      ) {
        localStorage.setItem("access_token", "undefined");
        throw new Error("access_token not exists.");
      }
      const { username } = await verifyToken();
      setUser(username);
    } catch (error) {
      console.error(error);
    }
  };
  const setDataInLocal = (user) => {
    const date = getToday();
    const wakeTime = localStorage.getItem("wakeTime");
    console.log("TO set Data: waketime", wakeTime);
    axios.post(`/api/${user}/${date}/worklist/record-time/wakeTime`, {
      value: wakeTime,
    });
  };
  useEffect(() => {
    setUserInToken();
  }, []);
  const authenticated = user != null;
  const login = async (userInfo, socialType) => {
    // setUser(signIn({ email, password }));
    try {
      const response = await axios.post(`/auth/login/${socialType}`, {
        userInfo,
      });
      const { access_token, username } = response.data;
      localStorage.setItem("access_token", JSON.stringify(access_token));
      setUser(username);
      setDataInLocal(username);
    } catch (error) {
      // alert(error);
      throw error;
    }
  };
  const logout = () => {
    setUser(null);
    localStorage.removeItem("access_token");
  };

  return [user, setUser, authenticated, login, logout];
};
