import axios from "axios";
import { useState } from "react";
import { useHistory } from "react-router";

const users = [
  { email: "kim@test.com", password: "123", name: "TEST" },
  { email: "lee@test.com", password: "456", name: "Lee" },
  { email: "park@test.com", password: "789", name: "Park" },
];
export const signIn = ({ email, password }) => {
  const user = users.find((user) => {
    // console.log("CHECK", user.email,em)
    return user.email === email && user.password === password;
  });
  if (!user) throw new Error("User not found");
  return user;
};
export const useLogin = () => {
  const [user, setUser] = useState(null);
  const history = useHistory();
  const authenticated = user != null;
  const onSetUser = (username) => {
    setUser(username);
    history.push("/");
  };
  const login = async ({ email, password }) => {
    // setUser(signIn({ email, password }));
    try {
      const response = await axios.post(`/auth/login/notSocial`, {
        email,
        password,
      });
      const { access_token, name } = response.data;
      localStorage.setItem("access_token", access_token);
      setUser(name);
      history.push("/");
    } catch (error) {
      alert(error);
      history.push("/");
    }
  };
  const logout = () => {
    onSetUser(null);
    localStorage.removeItem("access_token");
  };

  return [user, onSetUser, authenticated, login, logout];
};
