import { useTodo } from "../../hooks/todolist/useTodo";
import { getToday } from "../../tools/time";

export const Todo = ({ setTodo, isEditMode, todoItem }) => {
  const [
    onClickCheck,
    onChangeContent,
    onChangeDate,
    onChangePriority,
    onSubmitTodo,
    onEditTodo,
    onDeleteTodo,
  ] = useTodo(setTodo, todoItem);
  const inputBtn = isEditMode ? (
    <div>
      <button>E</button>
      <button>X</button>{" "}
    </div>
  ) : (
    <button onClick={onSubmitTodo}> S</button>
  );
  const inputElement = (
    <div>
      <input
        type="checkbox"
        name="todoCheck"
        defaultValue={todoItem?.isCheck}
        onChange={({ target: { value } }) => onClickCheck(value)}
      ></input>
      <input
        type="text"
        name="todoText"
        defaultValue={todoItem?.text}
        onChange={({ target: { value } }) => onChangeContent(value)}
      ></input>
      <input
        type="date"
        name="todoDate"
        defaultValue={todoItem?.date}
        min={getToday()}
        onChange={({ target: { value } }) => onChangeDate(value)}
      ></input>
      <input
        type="text"
        name="todoPriority"
        defaultValue={todoItem?.priority}
        onChange={({ target: { value } }) => onChangePriority(value)}
      ></input>
      {inputBtn}
    </div>
  );
  return <li> {inputElement}</li>;
};
