import { useState } from "react";

export const useTodo = (setTodo) => {
  const [isCheck, setCheck] = useState();
  const [content, setContent] = useState();
  const [date, setDate] = useState();
  const [priority, setPriority] = useState();

  const onClickCheck = () => setCheck(!isCheck);
  const onChangeContent = (content) => setContent(content);
  const onChangeDate = (date) => setDate(date);
  //   const onCheck = (event) => {
  //     try {
  //       event.preventDefault();
  //     } catch (error) {
  //       alert(error);
  //     }
  //   };

  const onChangePriority = (value) => setPriority(value);
  const onSubmitTodo = (event) => {
    const id = Date.now().toString(16) + randomToken.generate(5);
    setTodo().create();
  };
  return [onClickCheck, onChangeContent, onChangeDate, onChangePriority];
};
