import { useEffect, useState } from "react";
import { API } from "../../tools/axiosSetting";

export const useUpdateSetting = (user) => {
  console.log("UPDATE SETTING HOOK USER: ", user);
  const [targetWorkTime, setTargetWorkTime] = useState(0);
  const [targetWakeHour, setTargetWakeHour] = useState(0);
  const [targetBedHour, setTargetBedHour] = useState(0);
  const [targetWakeMinute, setTargetWakeMinute] = useState(0);
  const [targetBedMinute, setTargetBedMinute] = useState(0);

  const onChangeTargetWorkTime = (workTime) =>
    setTargetWorkTime(parseInt(workTime));
  const onChangeTargetWakeTime = (wakeHour) =>
    setTargetWakeHour(parseInt(wakeHour));
  const onChangeTargetBedTime = (bedHour) =>
    setTargetBedHour(parseInt(bedHour));
  const onChangeTargetWakeMinute = (wakeMinute) =>
    setTargetWakeTime(parseInt(wakeMinute));
  const onChangeTargetBedMinute = (bedMinute) =>
    setTargetBedTime(parseInt(bedMinute));

  const getAndUpdateSetting = async () => {
    try {
      const data = await API.get(`/auth/setting/${user}`);
      const settingObj = data?.data;
      if (!data) throw new Error("Cannot find data");
      console.log("SETTING DATA: ", settingObj);
      setTargetWorkTime(settingObj.targetWorkTime);
      setTargetWakeHour(settingObj.wakeHour);
      setTargetBedHour(settingObj.bedHour);
      setTargetWakeMinute(settingObj.wakeMinute);
      setTargetBedMinute(settingObj.bedMinute);
    } catch (error) {
      alert(error);
      // history.push("/setting");
    }
  };
  useEffect(() => {
    if (user) getAndUpdateSetting();
  }, [user]);

  const validator = (targetTimeObj) => {
    return (
      targetTimeObj.targetWorkTime &&
      targetTimeObj.targetWakeHour &&
      targetTimeObj.targetBedHour &&
      targetTimeObj.targetWakeMinute &&
      targetTimeObj.targetBedMinute
    );
  };
  const onEditTargetTime = (event) => {
    event.preventDefault();
    let willUpdate = true;
    const targetTimeObj = {
      targetWorkTime,
      targetWakeHour,
      targetBedHour,
      targetWakeMinute,
      targetBedMinute,
    };
    if (typeof validator === "function") willUpdate = validator(targetTimeObj);
    if (willUpdate) {
      console.log("SUBMIT: ", targetTimeObj);

      API.post(`/auth/setting/${user}`, {
        value: targetTimeObj,
        user,
      });
    } else {
      const targetWakeTime = targetWakeHour && targetWakeMinute;
      const targetBedTime = targetBedHour && targetBedMinute;
      let errText = `[ERROR] ${targetWorkTime ? "" : "TargetWorkTime"}${
        !targetWakeTime + !targetBedTime > 0 && !targetWorkTime ? ", " : ""
      }${targetWakeTime ? "" : "TargetWakeTime"}${
        !targetBedTime > 0 && !targetWakeTime ? ", " : ""
      }${targetBedTime ? "" : "TargetBedtime"} ${
        !targetWorkTime + !targetWakeTime + !targetBedTime > 1 ? "are" : "is"
      } not entered.`;
      alert(errText);
    }
  };
  const targetTimeObj = {
    targetWorkTime,
    targetWakeHour,
    targetBedHour,
    targetWakeMinute,
    targetBedMinute,
  };
  console.log("UPDATE SETTING HOOK targetTimeObj: ", targetTimeObj);
  return {
    onEditTargetTime,
    targetWorkTime,
    onChangeTargetWorkTime,
    targetTimeObj,
    targetWakeHour,
    setTargetWakeHour,
    targetBedHour,
    setTargetBedHour,
    targetWakeMinute,
    setTargetWakeMinute,
    targetBedMinute,
    setTargetBedMinute,
  };
};
