import axios from "axios";
import { useEffect, useState } from "react";

const verifyToken = async () => {
  const { token } = JSON.parse(localStorage.getItem("access_token"));
  console.log("local, storage's token: ", token);
  const result = await axios.post("/api/jwt/verify", { token });
  return result;
  // console.log(axios.post("/api/jwt/verify", token));
};
export const useLogin = () => {
  const [user, setUser] = useState();
  const setUserInToken = async () => {
    const initVal = await verifyToken().username;
    console.log(initVal);
    setUser(initVal);
  };
  useEffect(() => {
    setUserInToken();
  }, []);
  const authenticated = user != null;
  const login = async ({ email, password }) => {
    // setUser(signIn({ email, password }));
    try {
      const response = await axios.post(`/auth/login/notSocial`, {
        email,
        password,
      });
      const { access_token, username } = response.data;
      localStorage.setItem("access_token", JSON.stringify(access_token));
      setUser(username);
    } catch (error) {
      alert(error);
    }
  };
  const logout = () => {
    setUser(null);
    localStorage.removeItem("access_token");
  };

  return [user, setUser, authenticated, login, logout];
};
