import Calendar from "react-calendar";
import { changeFormatYYYYMMDD, getToday, UTCtoKTC } from "../../tools/time";
import "../../css/calendar.css";
import axios from "axios";
import { useDateInfo } from "../../hooks/calendar/useDateInfo";
export const CalendarTemplate = ({ user, updateDateHook }) => {
  // const clickDay = (event, date) => alert("Clicked day: ", date);
  const [date, onUpdateDate] = updateDateHook;
  const dateInfo = useDateInfo(user, date);
  const mark = ["2021-10-12", "2021-10-13", "2021-10-14"];
  console.log("[Calendar]");
  return (
    <article>
      <Calendar
        className="caldendar"
        onChange={onUpdateDate}
        value={date}
        locale={"en"}
        calendarType={"US"}
        onClickDay={(date, event) => {
          const localDateObj = {
            today: getToday(),
            clickedDate: UTCtoKTC(date),
          };
          localStorage.setItem("date", JSON.stringify(localDateObj));
        }}
        tileClassName={({ date, view }) => {
          if (
            dateInfo.find(
              (markedDate) => markedDate === changeFormatYYYYMMDD(date)
            )
          )
            return "highlight";
        }}
        //DB에 있는 날짜는 체크표시 해야함.
      />
    </article>
  );
};
