import Loading from "react-loading";
import StarRatings from "react-star-ratings";
import { StarRatingInput, StarRating, css } from "react-star-rating-input";

import DatePicker from "react-datepicker";
import { useTodoList } from "../../hooks/todolist/useTodoList";
import { LoadingElement } from "../Loading";
// import { InputTodo } from "./InputTodo";
import { Todo } from "./elements/Todo";
import "../../css/todoList.css";
// import { Example } from "./dateTest";

const todoExplain = (
  <div className="todo">
    <input
      id={`todo__checkbox_ex`}
      className="todo__checkbox todo__checkbox todo__content todo-display__content"
      type="checkbox"
      name="todoCheck"
    ></input>
    <label className="todo__label" for={`todo__checkbox_ex`}>
      {/* <span className="todo__label-icon">{isCheck ? checkElement : ""}</span> */}
    </label>
    <span className="todo__text todo-explain__text todo__content todo-explain__content">
      설명
    </span>
    <span className="todo__date todo-explain__text todo__content todo-explain__content">
      설명
    </span>
    <span className="todo__content todo__date todo-explain__content">설명</span>
    {/* {displayBtn} */}
  </div>
);
export const TodoList = ({ user, date }) => {
  const [todoList, setTodo, isTodoListLoading] = useTodoList(user, date);
  return isTodoListLoading ? (
    <LoadingElement text={"todoList is Loading"} />
  ) : (
    <ol className="todo-list">
      {todoExplain}
      <Todo setTodo={setTodo} _isEditMode={true} id={0} />
      {todoList &&
        Object.values(todoList).map((todoItem, idx) => {
          return (
            <Todo
              setTodo={setTodo}
              _isEditMode={true}
              todoItem={todoItem}
              key={idx}
              id={idx + 1}
            />
          );
        })}
    </ol>
  );
};
