import { useEffect, useState } from "react";
import { APIv2 } from "../../tools/API";
import { LocalStroage } from "../../tools/LocalStorage";
import { getAddedDateObj, getAddedDateStr } from "../../tools/time";
const STARTDATEDIFF = 2;
const makeStartDate = (date) => getAddedDateStr(date, -1 * STARTDATEDIFF);
const getStartDate = (date) =>
  LocalStroage.startDate().isEmpty()
    ? makeStartDate(date)
    : LocalStroage.startDate().get();
export const useListView = (user, date, setDate, workList) => {
  const [startDate, setStratDate] = useState(getStartDate(date));
  const getEndDate = (startDate) =>
    getAddedDateObj(startDate, STARTDATEDIFF * 2);
  const [listView, setListView] = useState();
  const [isListViewLoading, setLoad] = useState();

  const onClickLeftBtn = () => {
    const nextDate = getAddedDateStr(startDate, -1);
    LocalStroage.startDate().set(nextDate);
    setStratDate(nextDate);
  };
  const onClickRightBtn = () => {
    const nextDate = getAddedDateStr(startDate, 1);
    LocalStroage.startDate().set(nextDate);
    setStratDate(nextDate);
  };

  useEffect(() => {
    setStratDate(getStartDate(date));
  }, [date]);
  const getListView = async () => {
    try {
      if (user && startDate) {
        setLoad(true);
        const data = await APIv2.listView(user, startDate).get(STARTDATEDIFF);
        setLoad(false);
        if (!data?.data) throw new Error("List View cann't found");
        console.log("DATA : LISTVIEW ", data?.data);
        setListView(data?.data);
      }
    } catch (error) {
      alert(error);
    }
  };
  useEffect(() => {
    //setListView를 좀 더 스마트하게 바꾸어야 한다.
    console.log("Set list view start L or R");
  }, [startDate]);
  useEffect(() => {
    //setListView를 좀 더 스마트하게 바꾸어야 한다.
    console.log("Set list view start");
    getListView();
  }, [startDate]);
  return {
    isListViewLoading,
    startDate,
    listView,
    onClickLeftBtn,
    onClickRightBtn,
  };
};
