import { useState } from "react";
import Modal from "react-modal";
import { useDeleteWork } from "../../../../hooks/useDeleteWork";
import { useEditTime } from "../../../../hooks/useEditTime";
Modal.setAppElement("#root");
const customStyles = {
  content: {
    // top: "40vh",
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    backgroundColor: "rgb(245, 235, 220)",
    border: "none",
  },
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "rgba(0,0,0,0.4)",
  },
};
export const TimeRecordDisplay = ({ recordTime, isWake, updateRecrodTime }) => {
  const [editModalIsOpen, setEditModalIsOpen] = useState(false);
  const openEditModal = () => {
    setEditModalIsOpen(true);
    console.log("OPEN");
  };
  const closeEditModal = (event) => setEditModalIsOpen(false);
  const { onEditRecrodTime } = useEditTime(
    recordTime,
    updateRecrodTime,
    isWake,
    closeEditModal
  );
  return (
    <li
      className={
        isWake
          ? "workList__wakeTime-display workList__time-display workList__time workList__wakeTime"
          : "workList__bedTime-display workList__time-display workList__time workList__bedTime"
      }
      onClick={openEditModal}
    >
      {isWake ? "Wake at " : "Sleep at "}
      {recordTime.hour < 10 ? "0" + recordTime.hour : recordTime.hour}:
      {recordTime.minute < 10 ? "0" + recordTime.minute : recordTime.minute}
    </li>
  );
};
