import StarRatings from "react-star-ratings";
import { useTodo } from "../../hooks/todolist/useTodo";
import { getToday } from "../../tools/time";

export const Todo = ({ setTodo, isEditMode, todoItem }) => {
  const [
    onClickCheck,
    onChangeText,
    onChangeDate,
    onChangePriority,
    onSubmitTodo,
    onEditTodo,
    onDeleteTodo,
    isStopPriority,
    onChangeStopPriority,
  ] = useTodo(setTodo, todoItem);
  const inputBtn = isEditMode ? (
    <div className="todo-input__btn-box">
      <button
        className="todo-input__btn todo-input__btn-edit"
        onClick={onEditTodo}
      >
        E
      </button>
      <button
        className="todo-input__btn todo-input__btn-delete"
        onClick={onDeleteTodo}
      >
        X
      </button>{" "}
    </div>
  ) : (
    <div className="todo-input__btn-box">
      <button
        className="todo-input__btn todo-input__btn-add"
        onClick={isEditMode ? onEditTodo : onSubmitTodo}
      >
        Save
      </button>
    </div>
  );
  const inputElement = (
    <div className="todo-input">
      <input
        id="todo-input__checkbox"
        className="todo-input__checkbox todo-input__content"
        type="checkbox"
        name="todoCheck"
        defaultValue={isEditMode ? todoItem?.isCheck : undefined}
        onChange={({ target: { value } }) => onClickCheck(value)}
      ></input>
      <label for="todo-input_checkbox"></label>
      <input
        className="todo-input__text todo-input__content"
        type="text"
        name="todoText"
        defaultValue={isEditMode ? todoItem?.text : undefined}
        onChange={({ target: { value } }) => onChangeText(value)}
      ></input>
      <input
        className="todo-input__date todo-input__content"
        type="date"
        name="todoDate"
        defaultValue={isEditMode ? todoItem?.date : undefined}
        min={getToday()}
        onChange={({ target: { value } }) => onChangeDate(value)}
      ></input>
      {/* <input
        className="todo-input__priority todo-input__content"
        type="text"
        name="todoPriority"
        defaultValue={todoItem?.priority}
        onChange={({ target: { value } }) => onChangePriority(value)}
      ></input> */}
      <StarRatings
        // rating={todoItem?.priority}
        starRatedColor="rgba(241, 196, 15,0.8)"
        starEmptyColor="rgba(151, 151, 151, 0.707)"
        starHoverColor="rgba(241, 196, 15,0.9)"
        starSelectingHoverColor="red"
        changeRating={onChangePriority}
        numberOfStars={5}
        name="rating"
        starDimension="10px"
        starSpacing="0px"
      />{" "}
      {inputBtn}
    </div>
  );
  return (
    <li className="todo-item" key={"_" + todoItem?.id}>
      {" "}
      {inputElement}
    </li>
  );
};
